const App  = () => {

  const [category, setCategory] = useState("us")

  return (
      <div>
        <section className="heading">
          <h1 className="heading">WEBSITE NAME</h1>
          {NavBar(setCategory)}
        </section>
        <section className="articleLists">
          <ArticleList className="artricleList" category={category} />
        </section>
      </div>
    );
}

const handleNavBarClick = (cat) => {
  setCategory(cat)
}

export default App;
