  const [pageNumber, setPageNumber] = useState(1)

  const {loading, error, articles, hasMore} = useGetArticles(category, pageNumber)

  return (
      <ul>
        <div className="row">
          <div className="column">
            {articles.map( art => {
              return Article(art)})}
          </div>
